import { useState } from "react";
import React  from 'react'
import { Data } from './Data';
import styled from 'styled-components';
import { IconContext } from 'react-icons';
import {FiPlus, FiMinus} from 'react-icons/fi';
import { Params } from "./data";
import { Details } from "./data";


const Body = styled.body `
margin: 0;
height:100%;
background:  
rgba(66, 134, 244, 0.8);

@media (min-width:768px){
margin:0;
height: 100%;
background: none;
`;

const Header= styled.div`
width: 100%;
position: relative;
height: 150px;
background: #F9F9F9;

@media (min-width:768px){
position: relative;
width: 100%;
height: 183px;
background: #F9F9F9;
border: 1px solid #C4C4C4;
box-sizing: border-box;
position: relative;
};
`;

const Footer= styled.div`
width: 100%;
position: relative;
height: 150px;
background: #F9F9F9;
@media (min-width:768px){
    position: relative;
    width: 100%;
    height: 183px;
    left: 0px;
    top: 850px;
    background: #F9F9F9;
    border: 1px solid #C4C4C4;
    box-sizing: border-box;
};
`;


const Title = styled.div `
left: 20px;
width: 100%;
font-size: 28px;
text-align: center;
text-transform: uppercase;
color: black;
font-family: Open Sans;
font-style: normal;
font-weight: bold;
top: 29px;
margin-top: 10px;

@media (min-width:768px){
text-align: center;
text-transform: uppercase;
color: black;
font-family: Open Sans;
font-style: normal;
font-weight: bold;
font-size: 64px;
position: relative;
top: 260px;
};
`;


const Input = styled.input `
left:20px;
padding: 1em;
width: 90%;
color:rgba(0, 0, 0, 0.5);
background:rgba(255, 255, 255);
border: 1px solid #C3C7D2;
top:  437.93px;
font-size: 16px;

@media (min-width:768px){
padding: 1em;
width: 75%;
color:rgba(0, 0, 0, 0.5);
background:rgba(196, 196, 196, 0.2);
border: 1px solid #C3C7D2;
top:  350px;
position: relative;
left:175px;
font-size: 18px;
};
`;

const Help= styled.div `
left:20px;
width: 100%;
line-height: 25px;
align-items: baseline;
color:rgba(0, 0, 0, 0.5);
display: flex;
flex-direction: column;
flex-wrap: wrap;
font-family: Open Sans;
font-style: normal;
color: black;
text-align:center;
align-items:center;

@media (min-width:768px){
top: 400px;
position: relative;
color:rgba(0, 0, 0, 0.5);
display: flex;
flex-direction: column;
flex-wrap: wrap;
align-items: center;
font-family: Open Sans;
font-style: normal;
font-size:28px;
line-height: 2px;
};
`;

const Section1=styled.div `
text-align:center;
font-size: 24px;
position: relative;
display: inline-block;
font-weight: bold;
font-family: Open Sans;
font-style: normal;
text-transform: uppercase;

@media (min-width:768px){
    top: 600px;
    position: relative;
    left: 50px;
    font-size: 36px;
    font-weight: bold;
    font-family: Open Sans;
    font-style: normal;
    text-transform: uppercase;
    display: inline-block;
};
`;
const Section2= styled.div ` 
text-align:center;
font-size: 24px;
position: relative;
display: inline-block;
font-weight: bold;
font-family: Open Sans;
font-style: normal;
text-transform: uppercase;
left: 2.5em;

@media (min-width:768px){
    left: 50px;
    font-size: 36px;
    font-weight: bold;
    font-family: Open Sans;
    font-style: normal;
    text-transform: uppercase;
    top: 600px;
    position: relative;
    display: inline-block;
};
`;


const Section3 = styled.div `
text-align:center;
font-size: 24px;
position: relative;
display: inline-block;
font-weight: bold;
font-family: Open Sans;
font-style: normal;
text-transform: uppercase;
left: 5em;

@media (min-width:768px){
    display: inline-block;
    top: 600px;
    position: relative;
    left: 50px;
    font-size: 36px;
    font-weight: bold;
    font-family: Open Sans;
    font-style: normal;
    text-transform: uppercase;
};
`;

const AccordionSection = styled.div`
display:flex;
flex-direction: column;
top:675px;
font-size: 18px;

@media (min-width:768px){
    position: relative;
font-family: Open Sans;
font-style: normal;
font-weight: bold;
font-size: 28px;
display:flex;
flex-direction: column;
width:95%;
margin-left:2em;
top:575px;
};

`;



const Container = styled.div `

@media (min-width:768px){
    position: relative;
padding-bottom: 3em;
};

`;

const Wrap = styled.div  `
    font-size: 20px;
    font-family: Open Sans;
    font-style: normal;
    font-weight: bold;
    border: 1px solid #C3C7D2;
    background: #f9f9f9;

span{
    margin-right: 1em;
}
@media (min-width:768px){
    background: #f9f9f9;
color:black;
display:flex;
justify-content: space-between;
align-items: center;
width: 100%;
cursor: pointer
padding-left:4em;
border: 1px solid #C3C7D2;
font-size: 28px;
font-weight: bold;
};

span{
    margin-right: 1em;
}`;


const Dropdown = styled.div `
@media (min-width:768px){
    background: white;
color: black;
width: 98%;
height: 100px;
display:flex;
flex-wrap:wrap;
flex-direction: column;
justify-content:center
align-items:center;
font-size: 25px;
line-height: 35px;
padding-bottom: 3em;
padding-left: 1em;
};
`;

const Faq = () => {
const [clicked, setClicked]= useState (false)

const toggle= index =>{
    if(clicked === index){
        return setClicked(null)
    }

    setClicked(index)
}

    return (
        <Body>
            <Header> </Header>
            <Title>Frequently Asked Questions</Title>
            <Input  type='text' placeholder='What would you like to know?'/>
            <Help>
            <p>Click on the questions below</p>
             <p>If your question is not found, please message us through our Contact Page</p>
            </Help>
            <Section1>
            <p>Covid-19 Policies &amp; Protocols</p>
